function forceHTTPS()
    {
    if (isset($_SERVER['HTTPS']) && $_SERVER['HTTPS']) {
        return;
    } else {
        $requestURL = $_SERVER['HTTP_HOST'] . $_SERVER['REQUEST_URI'];
        header('HTTP/1.1 301 Moved Permanently');
        header("Location: https://$requestURL");
        exit();
    }
}
